function mobileNavigation() {
    $(".navigation.-mobile").toggleClass("-active")
}

function stickyNavigation() {
    $("header").before($("header > .header").clone().addClass("-sticky"));
    var e = $(".header.-sticky");
    $(window).scroll(function(t) {
        var a = $(window).scrollTop();
        a > 0 ? $(e).addClass("-stuck") : $(e).removeClass("-stuck")
    })
}

function usrDropdown(e) {
    $(e).nextAll(".usr-dropdown").toggleClass("-active")
}

function swiper(direction){
    if($(window).width() > 1024){
        if(direction == "left"){
            $(".sidebar.-touchable").animate({width: 0});
        } else {
            $(".sidebar.-touchable").animate({width: 90});
        }
    }
}

$(document).mouseup(function (e){
    var container = $(".usr-box");
    if (!container.is(e.target) 
    && container.has(e.target).length === 0) 
    {
        $(".usr-box .-active").removeClass("-active")
    }
});

$(document).ready(function() {
    new Clipboard('.fa-copy', {
        target: function(trigger) {
            return trigger.nextElementSibling;
        }
    });
    $("body").swipe( {
        swipe:function(event, direction, distance, duration, fingerCount, fingerData) {
            swiper(direction)
        }
    });
    $("[type='tel']").mask("(00) 0000-0000"), $(".specialChars").keyup(function(e) {
        /[0-9]/g.test(this.value) && (this.value = this.value.replace(/[0-9]/g, ""))
    }), $(window).width() > 1024 && (stickyNavigation(), $(window).scroll(function(e) {
        $("section").each(function(e, t) {
            var t = $(t);
            t.visible(!0) ? t.addClass("-animate") : t.removeClass("-animate")
        })
    })), $(window).on("resize", function() {
        $(".tcon-transform").removeClass("tcon-transform")
    }), $(".scroll").each(function() {
        $(this).on("click", function() {
            $("body").is(".pg-interna") ? window.location.replace("index.html#" + $(this).text().replace(/ /g, "-").replace("?", "").toLowerCase()) : $("html, body").stop(!0, !1).animate({
                scrollTop: $("section#" + latinize($(this).text().replace(/ /g, "-").replace("?", "").toLowerCase())).offset().top - 89
            }, 500)
        })
    })
});